const MeshBackground = ({ children }) => {
  return (
    <div className="relative min-h-screen w-full overflow-hidden bg-slate-950 text-white">
      <div className="pointer-events-none absolute inset-0">
        <div className="absolute -top-48 -left-48 h-[620px] w-[620px] rounded-full bg-[radial-gradient(circle_at_center,rgba(59,130,246,0.45),rgba(15,23,42,0)_70%)] blur-3xl mix-blend-screen animate-[spin_140s_linear_infinite]" />
        <div className="absolute top-1/4 -right-56 h-[520px] w-[520px] rounded-full bg-[radial-gradient(circle_at_center,rgba(168,85,247,0.45),rgba(15,23,42,0)_70%)] blur-3xl mix-blend-screen animate-[spin_200s_linear_infinite]" />
        <div className="absolute -bottom-56 left-1/3 h-[560px] w-[560px] rounded-full bg-[radial-gradient(circle_at_center,rgba(99,102,241,0.4),rgba(15,23,42,0)_70%)] blur-3xl mix-blend-screen animate-[spin_170s_linear_infinite]" />
        <div className="absolute inset-0 bg-[radial-gradient(circle_at_20%_20%,rgba(59,130,246,0.12),transparent_45%),radial-gradient(circle_at_80%_30%,rgba(139,92,246,0.12),transparent_40%),radial-gradient(circle_at_50%_80%,rgba(99,102,241,0.12),transparent_45%)]" />
        <div className="absolute inset-0 bg-gradient-to-b from-slate-950/40 via-slate-950/70 to-slate-950/90" />
      </div>
      <div className="relative">{children}</div>
    </div>
  );
};

export default MeshBackground;
