import { motion } from "framer-motion";
import { useState, useEffect } from "react";

const ThemeToggle = () => {
  const [isDark, setIsDark] = useState(() => {
    if (typeof window !== 'undefined') {
      const saved = localStorage.getItem('theme');
      return saved ? saved === 'dark' : true;
    }
    return true;
  });

  useEffect(() => {
    if (isDark) {
      document.documentElement.classList.add('dark');
      localStorage.setItem('theme', 'dark');
    } else {
      document.documentElement.classList.remove('dark');
      localStorage.setItem('theme', 'light');
    }
  }, [isDark]);

  const toggleTheme = () => {
    setIsDark(!isDark);
  };

  return (
    <motion.button
      onClick={toggleTheme}
      className="relative flex items-center gap-2 px-4 py-2 rounded-xl bg-light-surface dark:bg-dark-surface border border-light-border dark:border-dark-border hover:border-primary-500 dark:hover:border-primary-400 transition-all group"
      whileHover={{ scale: 1.05 }}
      whileTap={{ scale: 0.95 }}
      aria-label="Toggle theme"
    >
      {/* Toggle Track */}
      <div className="relative w-14 h-7 bg-light-bg-tertiary dark:bg-dark-bg-tertiary rounded-full transition-colors">
        {/* Animated Background Glow */}
        <motion.div
          className="absolute inset-0 rounded-full"
          style={{
            background: isDark
              ? 'linear-gradient(135deg, rgba(37, 99, 235, 0.3), rgba(6, 182, 212, 0.3))'
              : 'linear-gradient(135deg, rgba(251, 191, 36, 0.3), rgba(245, 158, 11, 0.3))'
          }}
          animate={{
            opacity: [0.5, 1, 0.5],
          }}
          transition={{
            duration: 2,
            repeat: Infinity,
            ease: "easeInOut"
          }}
        />

        {/* Toggle Knob */}
        <motion.div
          className="absolute top-1 flex items-center justify-center w-5 h-5 bg-white dark:bg-dark-bg-primary rounded-full shadow-lg"
          animate={{
            x: isDark ? 28 : 4,
          }}
          transition={{
            type: "spring",
            stiffness: 500,
            damping: 30
          }}
        >
          {/* Icon Container */}
          <div className="relative w-4 h-4">
            {/* Sun Icon */}
            <motion.svg
              className="absolute inset-0 w-4 h-4 text-amber-500"
              fill="currentColor"
              viewBox="0 0 20 20"
              animate={{
                scale: isDark ? 0 : 1,
                rotate: isDark ? 180 : 0,
                opacity: isDark ? 0 : 1,
              }}
              transition={{ duration: 0.3 }}
            >
              <path
                fillRule="evenodd"
                d="M10 2a1 1 0 011 1v1a1 1 0 11-2 0V3a1 1 0 011-1zm4 8a4 4 0 11-8 0 4 4 0 018 0zm-.464 4.95l.707.707a1 1 0 001.414-1.414l-.707-.707a1 1 0 00-1.414 1.414zm2.12-10.607a1 1 0 010 1.414l-.706.707a1 1 0 11-1.414-1.414l.707-.707a1 1 0 011.414 0zM17 11a1 1 0 100-2h-1a1 1 0 100 2h1zm-7 4a1 1 0 011 1v1a1 1 0 11-2 0v-1a1 1 0 011-1zM5.05 6.464A1 1 0 106.465 5.05l-.708-.707a1 1 0 00-1.414 1.414l.707.707zm1.414 8.486l-.707.707a1 1 0 01-1.414-1.414l.707-.707a1 1 0 011.414 1.414zM4 11a1 1 0 100-2H3a1 1 0 000 2h1z"
                clipRule="evenodd"
              />
            </motion.svg>

            {/* Moon Icon */}
            <motion.svg
              className="absolute inset-0 w-4 h-4 text-primary-400"
              fill="currentColor"
              viewBox="0 0 20 20"
              animate={{
                scale: isDark ? 1 : 0,
                rotate: isDark ? 0 : -180,
                opacity: isDark ? 1 : 0,
              }}
              transition={{ duration: 0.3 }}
            >
              <path d="M17.293 13.293A8 8 0 016.707 2.707a8.001 8.001 0 1010.586 10.586z" />
            </motion.svg>
          </div>
        </motion.div>
      </div>

      {/* Text Label (Hidden on mobile) */}
      <span className="hidden sm:block text-sm font-medium text-light-text-secondary dark:text-dark-text-secondary group-hover:text-primary-600 dark:group-hover:text-primary-400 transition-colors">
        {isDark ? 'Dark' : 'Light'}
      </span>

      {/* Glow Effect on Hover */}
      <motion.div
        className="absolute inset-0 rounded-xl opacity-0 group-hover:opacity-100 transition-opacity pointer-events-none"
        style={{
          background: isDark
            ? 'radial-gradient(circle at center, rgba(37, 99, 235, 0.1), transparent)'
            : 'radial-gradient(circle at center, rgba(251, 191, 36, 0.1), transparent)',
          filter: 'blur(8px)',
        }}
      />
    </motion.button>
  );
};

export default ThemeToggle;
