import React from 'react';

const ScrollToTop = () => {

  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: 'smooth'
    });
  };

  return (
    <button
      onClick={scrollToTop}
      className="fixed top-1/4 right-6 z-50 p-4 bg-blue-600 hover:bg-blue-700 text-white rounded-full shadow-xl transform -translate-y-1/2 transition-all duration-200 border-2 border-blue-400"
      title="Scroll to top"
      style={{ zIndex: 9999 }}
    >
      <svg 
        className="w-5 h-5" 
        fill="none" 
        stroke="currentColor" 
        viewBox="0 0 24 24"
      >
        <path 
          strokeLinecap="round" 
          strokeLinejoin="round" 
          strokeWidth={3} 
          d="M5 10l7-7m0 0l7 7m-7-7v18" 
        />
      </svg>
    </button>
  );
};

export default ScrollToTop;
