import { motion } from "framer-motion";

const About = () => {
  const technologies = [
    { name: "React", icon: "笞幢ｸ十

MySQL", icon: "汾ｬ" },
    { name: "Git", icon: "沒ｦ" },
    { name: "VS Code", icon: "汳ｻ" },
    { name: "Figma", icon: "沁ｨ" },
  ];

  return (
    <section id="about" className="relative py-16 md:py-20 bg-dark-bg overflow-hidden">
      {/* Background Decoration */}
      <div className="absolute inset-0 pointer-events-none">
        <div className="absolute top-0 right-0 w-64 h-64 bg-gradient-to-bl from-blue-500/10 to-transparent rounded-full blur-3xl" />
      </div>

      <div className="relative z-10 mx-auto max-w-6xl px-4 sm:px-6 md:px-8">
        <motion.div
          className="text-center mb-8"
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.6 }}
        >
          <h3 className="text-blue-400 text-xs sm:text-sm font-semibold mb-2 uppercase tracking-wider">My Toolkit</h3>
          <h2 className="text-2xl sm:text-3xl md:text-4xl font-bold text-white">Tools and Technologies I Use</h2>
        </motion.div>

        <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-5 gap-3 sm:gap-4">
          {technologies.map((tech, index) => (
            <motion.div
              key={tech.name}
              className="bg-dark-surface border border-gray-700 rounded-xl p-4 hover:border-blue-500/50 hover:shadow-lg hover:shadow-blue-500/10 transition-all duration-300 group"
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.5, delay: index * 0.05 }}
              whileHover={{ y: -5 }}
            >
              <div className="text-center">
                <div className="text-3xl mb-2 group-hover:scale-110 transition-transform">{tech.icon}</div>
                <h3 className="text-sm font-semibold text-white">{tech.name}</h3>
              </div>
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default About;
