import { useEffect, useRef } from "react";

const ParticlesBackground = () => {
  const canvasRef = useRef(null);
  const animationRef = useRef(null);
  const particlesRef = useRef([]);
  const sizeRef = useRef({ width: 0, height: 0, dpr: 1 });

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) {
      return undefined;
    }

    const ctx = canvas.getContext("2d");
    if (!ctx) {
      return undefined;
    }

    const initCanvas = () => {
      const dpr = Math.min(window.devicePixelRatio || 1, 2);
      const { innerWidth, innerHeight } = window;

      sizeRef.current = { width: innerWidth, height: innerHeight, dpr };
      canvas.width = innerWidth * dpr;
      canvas.height = innerHeight * dpr;
      canvas.style.width = `${innerWidth}px`;
      canvas.style.height = `${innerHeight}px`;
      ctx.setTransform(dpr, 0, 0, dpr, 0, 0);
    };

    const createParticles = () => {
      const { width, height } = sizeRef.current;
      const density = Math.max(Math.floor((width * height) / 22000), 60);

      particlesRef.current = Array.from({ length: density }, () => {
        const speed = 0.12 + Math.random() * 0.4;
        const angle = Math.random() * Math.PI * 2;

        return {
          x: Math.random() * width,
          y: Math.random() * height,
          radius: 0.6 + Math.random() * 1.4,
          alpha: 0.22 + Math.random() * 0.5,
          vx: Math.cos(angle) * speed,
          vy: Math.sin(angle) * speed,
          twinkle: 0.001 + Math.random() * 0.006
        };
      });
    };

    const draw = () => {
      const { width, height } = sizeRef.current;
      ctx.clearRect(0, 0, width, height);

      ctx.fillStyle = "rgba(255, 255, 255, 0.65)";
      particlesRef.current.forEach((particle) => {
        particle.x += particle.vx;
        particle.y += particle.vy;

        if (particle.x < -20) particle.x = width + 20;
        if (particle.x > width + 20) particle.x = -20;
        if (particle.y < -20) particle.y = height + 20;
        if (particle.y > height + 20) particle.y = -20;

        particle.alpha += particle.twinkle;
        if (particle.alpha >= 0.8 || particle.alpha <= 0.18) {
          particle.twinkle *= -1;
        }

        ctx.beginPath();
        ctx.globalAlpha = particle.alpha;
        ctx.shadowColor = "rgba(120, 180, 255, 0.65)";
        ctx.shadowBlur = 12;
        ctx.arc(particle.x, particle.y, particle.radius, 0, Math.PI * 2);
        ctx.fill();
      });

      ctx.globalAlpha = 1;
      animationRef.current = requestAnimationFrame(draw);
    };

    const handleResize = () => {
      initCanvas();
      createParticles();
    };

    initCanvas();
    createParticles();
    draw();

    window.addEventListener("resize", handleResize);

    return () => {
      window.removeEventListener("resize", handleResize);
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
      }
    };
  }, []);

  return (
    <div className="particle-wrap" aria-hidden="true">
      <canvas ref={canvasRef} className="particle-canvas" />
    </div>
  );
};

export default ParticlesBackground;
