import { createPortal } from "react-dom";

const ScrollToTop = () => {
  const handleClick = () => {
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  if (typeof document === "undefined") {
    return null;
  }

  return createPortal(
    <button
      id="scrollToTop"
      type="button"
      aria-label="Scroll to top"
      onClick={handleClick}
    >
      Top
    </button>,
    document.body
  );
};

export default ScrollToTop;
