import { useState, useEffect } from 'react';

const ScrollToTop = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [showPrompt, setShowPrompt] = useState(false);

  useEffect(() => {
    const toggleVisibility = () => {
      if (window.pageYOffset > 100) {
        setIsVisible(true);
      } else {
        setIsVisible(false);
      }
    };

    window.addEventListener('scroll', toggleVisibility);
    return () => window.removeEventListener('scroll', toggleVisibility);
  }, []);

  const handleScrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: 'smooth'
    });
    setShowPrompt(false);
  };

  if (!isVisible) return null;

  return (
    <>
      <button
        onClick={() => setShowPrompt(true)}
        className="fixed top-1/2 right-6 z-50 p-4 bg-blue-600 hover:bg-blue-700 text-white rounded-full shadow-xl transform -translate-y-1/2 transition-all duration-200 border-2 border-blue-400"
        title="Scroll to top"
        style={{ zIndex: 9999 }}
      >
        <svg 
          className="w-5 h-5" 
          fill="none" 
          stroke="currentColor" 
          viewBox="0 0 24 24"
        >
          <path 
            strokeLinecap="round" 
            strokeLinejoin="round" 
            strokeWidth={3} 
            d="M5 10l7-7m0 0l7 7m-7-7v18" 
          />
        </svg>
      </button>

      {/* Custom Prompt Modal */}
      {showPrompt && (
        <div className="fixed inset-0 z-[60] flex items-center justify-center bg-black bg-opacity-50 backdrop-blur-sm">
          <div className="bg-white p-6 rounded-2xl shadow-2xl max-w-sm w-full mx-4 transform transition-all">
            <h3 className="text-xl font-bold text-gray-900 mb-2">Scroll to Top?</h3>
            <p className="text-gray-600 mb-6">
              Would you like to return to the top of the page?
            </p>
            <div className="flex space-x-3">
              <button
                onClick={() => setShowPrompt(false)}
                className="flex-1 px-4 py-2 bg-gray-100 hover:bg-gray-200 text-gray-800 rounded-lg font-medium transition-colors"
              >
                Cancel
              </button>
              <button
                onClick={handleScrollToTop}
                className="flex-1 px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-lg font-medium transition-colors"
              >
                Yes, Go Up
              </button>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default ScrollToTop;
