const MeshBackground = ({ children }) => {
  return (
    <div className="relative isolate min-h-screen w-full overflow-hidden bg-slate-950 text-white">
      <div className="pointer-events-none absolute inset-0">
        <div className="absolute inset-0 bg-[radial-gradient(1200px_circle_at_15%_10%,rgba(59,130,246,0.25),transparent_60%),radial-gradient(1000px_circle_at_85%_20%,rgba(168,85,247,0.22),transparent_60%),radial-gradient(1100px_circle_at_50%_85%,rgba(99,102,241,0.2),transparent_60%)] bg-[length:200%_200%] animate-mesh" />
        <div className="absolute -top-56 -left-56 h-[700px] w-[700px] rounded-full bg-[radial-gradient(circle_at_center,rgba(59,130,246,0.55),rgba(15,23,42,0)_70%)] blur-3xl mix-blend-screen animate-mesh-spin" />
        <div className="absolute top-1/4 -right-64 h-[560px] w-[560px] rounded-full bg-[radial-gradient(circle_at_center,rgba(168,85,247,0.5),rgba(15,23,42,0)_70%)] blur-3xl mix-blend-screen animate-[spin_220s_linear_infinite]" />
        <div className="absolute -bottom-64 left-1/3 h-[600px] w-[600px] rounded-full bg-[radial-gradient(circle_at_center,rgba(99,102,241,0.45),rgba(15,23,42,0)_70%)] blur-3xl mix-blend-screen animate-[spin_190s_linear_infinite]" />
        <div className="absolute inset-0 bg-gradient-to-b from-slate-950/40 via-slate-950/70 to-slate-950/90" />
      </div>
      <div className="relative">{children}</div>
    </div>
  );
};

export default MeshBackground;
