import { useState, useEffect } from 'react';

const ScrollToTop = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [scrollProgress, setScrollProgress] = useState(0);

  useEffect(() => {
    const toggleVisibility = () => {
      const scrolled = document.documentElement.scrollTop;
      const maxHeight = document.documentElement.scrollHeight - document.documentElement.clientHeight;
      const scrollPercent = (scrolled / maxHeight) * 100;
      
      if (scrolled > 200) {
        setIsVisible(true);
        setScrollProgress(scrollPercent);
      } else {
        setIsVisible(false);
        setScrollProgress(0);
      }
    };

    window.addEventListener('scroll', toggleVisibility);
    return () => window.removeEventListener('scroll', toggleVisibility);
  }, []);

  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: 'smooth'
    });
  };

  if (!isVisible) return null;

  return (
    <button
      onClick={scrollToTop}
      className="fixed bottom-8 right-8 z-50 p-4 bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 text-white rounded-full shadow-xl transition-all duration-300 border-2 border-blue-400 hover:border-purple-400 hover:scale-110 group"
      title="Scroll to top"
      style={{ 
        zIndex: 9999,
        background: `conic-gradient(from 0deg, #3b82f6 ${scrollProgress}%, #e5e7eb ${scrollProgress}%)`
      }}
    >
      <div className="absolute inset-1 bg-gradient-to-r from-blue-600 to-purple-600 rounded-full flex items-center justify-center">
        <svg 
          className="w-5 h-5 group-hover:animate-bounce" 
          fill="none" 
          stroke="currentColor" 
          viewBox="0 0 24 24"
        >
          <path 
            strokeLinecap="round" 
            strokeLinejoin="round" 
            strokeWidth={3} 
            d="M5 10l7-7m0 0l7 7m-7-7v18" 
          />
        </svg>
      </div>
    </button>
  );
};

export default ScrollToTop;
