import { motion } from "framer-motion";

const About = () => {
  const technologies = [
    { name: "React", icon: "笞幢ｸ十

MySQL", icon: "汾ｬ" },
    { name: "Git", icon: "沒ｦ" },
    { name: "VS Code", icon: "汳ｻ" },
    { name: "Figma", icon: "沁ｨ" },
  ];

  return (
    <section id="about" className="relative py-32 dark:bg-dark-bg overflow-hidden">
      {/* Background Decoration */}
      <div className="absolute inset-0 pointer-events-none">
        <div className="absolute top-0 right-0 w-96 h-96 bg-gradient-to-bl from-blue-500/10 to-transparent rounded-full blur-3xl" />
      </div>

      <div className="relative z-10 mx-auto max-w-7xl px-6 md:px-8">
        <motion.div
          className="mb-16 text-center"
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.6 }}
        >
          <h3 className="text-blue-600 dark:text-blue-400 text-sm font-semibold mb-4 uppercase tracking-wider">Introduction</h3>
          <h2 className="text-4xl md:text-5xl lg:text-6xl font-bold mb-6 text-gray-900 dark:text-white">About me</h2>
          <p className="text-base md:text-lg leading-relaxed text-gray-700 dark:text-gray-300 max-w-4xl mx-auto">
            Our team is proficient in a wide range of technologies for building web and mobile applications. We have a strong foundation in programming languages including <span className="text-blue-600 dark:text-blue-400 font-semibold">HTML, CSS, JavaScript, PHP, C, Java, React Js, and Next Js</span>. Additionally, we utilize modern tools and technologies such as Visual Studio Code, Figma, Git, and more. With a BSc (Hons) in Computing and Information Systems, we have successfully completed more than 5 projects.
          </p>
        </motion.div>

        <div className="grid gap-6 md:grid-cols-3 mt-12">
          {/* Languages Card */}
          <motion.div
            className="bg-gray-50 dark:bg-gray-800/50 border border-gray-200 dark:border-gray-700/50 rounded-2xl p-6 md:p-8 hover:border-blue-500/50 hover:shadow-lg hover:shadow-blue-500/10 dark:hover:shadow-blue-500/20 transition-all duration-300 group"
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.6, delay: 0.1 }}
          >
            <div className="w-14 h-14 bg-blue-100 dark:bg-blue-500/20 rounded-full flex items-center justify-center mb-5 group-hover:bg-blue-500/20 dark:group-hover:bg-blue-500/30 transition-colors">
              <svg className="w-8 h-8 text-blue-600 dark:text-blue-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10 20l4-16m4 4l4 4-4 4M6 16l-4-4 4-4" />
              </svg>
            </div>
            <h3 className="text-xl md:text-2xl font-bold mb-3 text-gray-900 dark:text-white">Languages</h3>
            <p className="text-sm md:text-base text-gray-600 dark:text-gray-300 leading-relaxed">HTML, CSS, JavaScript, PHP, C, Java, React Js, Next Js</p>
          </motion.div>

          {/* Education Card */}
          <motion.div
            className="bg-gray-50 dark:bg-gray-800/50 border border-gray-200 dark:border-gray-700/50 rounded-2xl p-6 md:p-8 hover:border-blue-500/50 hover:shadow-lg hover:shadow-blue-500/10 dark:hover:shadow-blue-500/20 transition-all duration-300 group"
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.6, delay: 0.2 }}
          >
            <div className="w-14 h-14 bg-blue-100 dark:bg-blue-500/20 rounded-full flex items-center justify-center mb-5 group-hover:bg-blue-500/20 dark:group-hover:bg-blue-500/30 transition-colors">
              <svg className="w-8 h-8 text-blue-600 dark:text-blue-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 14l9-5-9-5-9 5 9 5z" />
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 14l6.16-3.422a12.083 12.083 0 01.665 6.479A11.952 11.952 0 0012 20.055a11.952 11.952 0 00-6.824-2.998 12.078 12.078 0 01.665-6.479L12 14z" />
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 14l9-5-9-5-9 5 9 5zm0 0l6.16-3.422a12.083 12.083 0 01.665 6.479A11.952 11.952 0 0012 20.055a11.952 11.952 0 00-6.824-2.998 12.078 12.078 0 01.665-6.479L12 14zm-4 6v-7.5l4-2.222" />
              </svg>
            </div>
            <h3 className="text-xl md:text-2xl font-bold mb-3 text-gray-900 dark:text-white">Education</h3>
            <p className="text-sm md:text-base text-gray-600 dark:text-gray-300 leading-relaxed">BSc (Hons) in Computing and Information Systems</p>
          </motion.div>

          {/* Projects Card */}
          <motion.div
            className="bg-gray-50 dark:bg-gray-800/50 border border-gray-200 dark:border-gray-700/50 rounded-2xl p-6 md:p-8 hover:border-blue-500/50 hover:shadow-lg hover:shadow-blue-500/10 dark:hover:shadow-blue-500/20 transition-all duration-300 group"
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.6, delay: 0.3 }}
          >
            <div className="w-14 h-14 bg-blue-100 dark:bg-blue-500/20 rounded-full flex items-center justify-center mb-5 group-hover:bg-blue-500/20 dark:group-hover:bg-blue-500/30 transition-colors">
              <svg className="w-8 h-8 text-blue-600 dark:text-blue-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 13.255A23.931 23.931 0 0112 15c-3.183 0-6.22-.62-9-1.745M16 6V4a2 2 0 00-2-2h-4a2 2 0 00-2 2v2m4 6h.01M5 20h14a2 2 0 002-2V8a2 2 0 00-2-2H5a2 2 0 00-2 2v10a2 2 0 002 2z" />
              </svg>
            </div>
            <h3 className="text-xl md:text-2xl font-bold mb-3 text-gray-900 dark:text-white">Projects</h3>
            <p className="text-sm md:text-base text-gray-600 dark:text-gray-300 leading-relaxed">Built more than 5 projects</p>
          </motion.div>
        </div>

        {/* Tools and Technologies Section - Smaller */}
        <motion.div
          className="mt-16"
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.6 }}
        >
          <h3 className="text-blue-600 dark:text-blue-400 text-xs sm:text-sm font-semibold mb-6 uppercase tracking-wider text-center">Tools & Technologies I Use</h3>
          
          <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-5 gap-3 sm:gap-4 max-w-4xl mx-auto">
            {technologies.map((tech, index) => (
              <motion.div
                key={tech.name}
                className="bg-white dark:bg-dark-card border border-gray-200 dark:border-gray-700 rounded-xl p-3 sm:p-4 hover:border-blue-500 dark:hover:border-blue-500/50 hover:shadow-lg hover:shadow-blue-500/20 dark:hover:shadow-blue-500/10 transition-all duration-300 group"
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                viewport={{ once: true }}
                transition={{ duration: 0.5, delay: index * 0.05 }}
                whileHover={{ y: -5 }}
              >
                <div className="text-center">
                  <div className="text-2xl sm:text-3xl mb-2 group-hover:scale-110 transition-transform">{tech.icon}</div>
                  <h4 className="text-xs sm:text-sm font-semibold text-gray-800 dark:text-white">{tech.name}</h4>
                </div>
              </motion.div>
            ))}
          </div>
        </motion.div>
      </div>
    </section>
  );
};

export default About;
